import * as React from 'react';
import { extend, useThree, useFrame } from 'react-three-fiber';
import { TrackballControls } from 'three/examples/jsm/controls/TrackballControls';
import { FlyControls } from 'three/examples/jsm/controls/FlyControls';
import * as THREE from 'three';

// extend THREE to include TrackballControls
extend({ TrackballControls });

// key code constants
// const ALT_KEY = 18;
// const CTRL_KEY = 17;
// const CMD_KEY = 91;

const Controls = ({ }) => {
    const controls = React.useRef();
    const { camera, gl } = useThree();


    // https://discourse.threejs.org/t/how-to-limit-pan-in-orbitcontrols-for-orthographiccamera/9061/15

  useFrame(() => {
        var minPan = new THREE.Vector3(-15, -15, -15);
        var maxPan = new THREE.Vector3(50, 50, 50);
        var _v = new THREE.Vector3();

          _v.copy(controls.target);
          controls.target.clamp(minPan, maxPan);
          _v.sub(controls.target);
          camera.position.sub(_v);

    controls.current.update();
  });


    return (
      <trackballControls
        ref={controls}
        args={[camera, gl.domElement]}
        dynamicDampingFactor={0.5}
        zoomSpeed={0.5}
        minDistance={43}
        maxDistance={50}

        mouseButtons={{
          LEFT: THREE.MOUSE.PAN, // make pan the default instead of rotate
          MIDDLE: THREE.MOUSE.ZOOM,
          RIGHT: THREE.MOUSE.ROTATE,
        }}
      />
    );
};

export default Controls;
