import React, { useRef, useEffect } from 'react';
import mapboxgl from 'mapbox-gl'; 
import styles from './Map.module.css';
import { useObserver } from 'mobx-react-lite';
// import data from './../../data/region.json' 
mapboxgl.accessToken = 'pk.eyJ1IjoiY2FyZG9lbmxpZW4iLCJhIjoiY2tiODI3Znl4MDAyazJ4cXJ6cWNvdWswcSJ9.aVAGrbiyl5I5yb5KROaD7A';

const Map = () => {
    const mapContainerRef = useRef(null);
    const regionsJson = require('./../../data/region.json');

      useEffect(() => {
        const map = new mapboxgl.Map({
          container: mapContainerRef.current,
          style: 'mapbox://styles/mapbox/dark-v10',
            center: [13.356, 34.047],
            zoom: 1.5,
        });

        let hoveredRegionId = null;
        map.on("load", function() {
            console.log("load")
          map.addSource('regions', {
            type: 'geojson',
            data: regionsJson,
          });

          map.addLayer({
            id: 'regions-layer',
            type: 'fill',
            source: 'regions',
            paint: {
              'fill-color': 'rgba(200, 100, 240, 0.4)',
              'fill-outline-color': 'rgba(200, 100, 240, 1)',
              'fill-opacity': [
                'case',
                ['boolean', ['feature-state', 'zoom'], false],
                0.2,
                1,
              ],
            },
          });
        });

        return () => map.remove();
      }, []); 

      return (
        <div className={styles.mapContainer} ref={mapContainerRef} />  
      );
};

export default Map;
