import React, { useRef, useEffect } from 'react';
import mapboxgl from 'mapbox-gl'; // or "const mapboxgl = require('mapbox-gl');"
import styles from './Map.module.css';
import { useObserver } from 'mobx-react-lite';
mapboxgl.accessToken = 'pk.eyJ1IjoiY2FyZG9lbmxpZW4iLCJhIjoiY2tiODI3Znl4MDAyazJ4cXJ6cWNvdWswcSJ9.aVAGrbiyl5I5yb5KROaD7A';

const Map = () => {
    const mapContainerRef = useRef(null);

    // const map = new mapboxgl.Map({
    //   container: 'map',
    //   style: 'mapbox://styles/mapbox/dark-v10',
    //   center: [13.356, 34.047],
    //   zoom: 1.5,
    // });

//   return (
//     <div id={styles.map}></div>
//   );


      useEffect(() => {
        const map = new mapboxgl.Map({
          container: mapContainerRef.current,
          // See style options here: https://docs.mapbox.com/api/maps/#styles
          style: 'mapbox://styles/mapbox/streets-v11',
          center: [-104.9876, 39.7405],
          zoom: 12.5,
        });

        // add navigation control (the +/- zoom buttons)
        map.addControl(new mapboxgl.NavigationControl(), 'bottom-right');

        // clean up on unmount
        return () => map.remove();
      }, []); // eslint-disable-line react-hooks/exhaustive-deps

      return <div className={styles.mapContainer} ref={mapContainerRef} />;
};

export default Map;
