import React, { useState } from 'react';
import { useSpring, a } from 'react-spring/three';
import * as THREE from 'three';
import { HTML, Text } from 'drei';
import styles from '../Ancestor/Ancestor.module.css';

const Ancestor = ({ancestor}) => {
  // This reference will give us direct access to the mesh
  const img = new THREE.TextureLoader().load('./assets/img/ancestor_george.png');

  // Set up state for the hovered and active state
  const [hovered, setHover] = useState(false);

  const states = useSpring({
    scale: hovered ? [1.1, 1.1, 1] : [1, 1, 1],
  });

  // const position = parseInt(ancestor.coordinates.split(`,`), 10);
  // console.log(position)
  // const posX = position[0];
  // const posY = position[1];
  // const posZ = position[2];

  let z = ancestor.coordinates.split(',');
  var newAr = z.map((item) => (parseInt(item) ? parseInt(item) : item)); 
  console.log(newAr)

  return (
    <a.mesh
      onPointerOver={(e) => setHover(true)}
      onPointerOut={(e) => setHover(false)}
      scale={states.scale}
      position={[-2, 0, 12]}
    >
      <planeGeometry attach="geometry" args={[1, 1]} />
      <a.meshBasicMaterial attach="material" map={img} transparent={true} />

      <Text
        fontSize={0.15}
        color={'white'}
        depthOffset={10}
        anchorX="70%"
        anchorY="400%"
        font={
          'https://fonts.gstatic.com/s/raleway/v14/1Ptrg8zYS_SKggPNwK4vaqI.woff'
        }
      >
        {ancestor.name}
      </Text>
      <Text fontSize={0.1} color={'white'} anchorX="135%" anchorY="440%">
        1850 - 1940
      </Text>
    </a.mesh>
  );
};

export default Ancestor;



