import React, { useRef, useEffect } from 'react';
import mapboxgl from 'mapbox-gl'; 
import styles from './Map.module.css';
import { useObserver } from 'mobx-react-lite';
// import data from './../../data/region.json' 
mapboxgl.accessToken = 'pk.eyJ1IjoiY2FyZG9lbmxpZW4iLCJhIjoiY2tiODI3Znl4MDAyazJ4cXJ6cWNvdWswcSJ9.aVAGrbiyl5I5yb5KROaD7A';

const Map = () => {
    const mapContainerRef = useRef(null);
    const regionsJson = require('./../../data/region.json');

      useEffect(() => {
        const map = new mapboxgl.Map({
          container: mapContainerRef.current,
          style: 'mapbox://styles/mapbox/dark-v10',
            center: [13.356, 34.047],
            zoom: 1.5,
        });

        let hoveredRegionId = null;
        map.on("load", function() {
            console.log("load")
          map.addSource('regions', {
            type: 'geojson',
            data: regionsJson,
          });

          map.addLayer({
            id: 'regions-layer',
            type: 'fill',
            source: 'regions',
            paint: {
              'fill-color': 'rgba(200, 100, 240, 0.4)',
              'fill-outline-color': 'rgba(200, 100, 240, 1)',
              'fill-opacity': [
                'case',
                ['boolean', ['feature-state', 'zoom'], false],
                0.2,
                1,
              ],
            },
          });

            // ADD BORDER
            map.addLayer({
                id: "region-borders",
                type: "line",
                source: "regions",
                layout: {},
                paint: {
                "line-color": "rgba(200, 100, 240, 0.8)",
                "line-width": 2,
                "line-opacity": [
                    "case",
                    ["boolean", ["feature-state", "zoom"], false],
                    0.4,
                    1
                ]
                }
            });

            map.addLayer({
                id: "region-info-percentage",
                source: "regions",
                type: "symbol",
                layout: {
                "text-field": ["concat", ["to-string", ["get", "ROOTS_PERCENTAGE"]], "%"],
                "text-font": ["Open Sans Bold", "Arial Unicode MS Bold"],
                "text-size": 20
                },
                paint: {
                "text-color": "rgba(255,255,255,1)"
                }
            });

            map.addLayer({
                id: "region-info-amount",
                source: "regions",
                type: "symbol",
                layout: {
                "text-field": [
                    "concat",
                    ["to-string", ["get", "NUMBER_ANCESTORS"]],
                    " ancestors"
                ],
                "text-font": ["Open Sans Regular", "Arial Unicode MS Regular"],
                "text-size": 14,
                "text-offset": [0, 3]
                },
                paint: {
                "text-color": "rgba(255,255,255,1)"
                }
            });



        });

        return () => map.remove();
      }, []); 

      return (
        <div className={styles.mapContainer} ref={mapContainerRef} />  
      );
};

export default Map;
